// Import the ethers library
import React from "react";
import web3 from "web3";
import "bootstrap/dist/css/bootstrap.css";
import "bootstrap/dist/css/bootstrap-grid.css";

const onClickFunction = async (e) => {
  e.preventDefault();
  //nfttoken.setExcluded(address, true);

  console.log("getBlockchain");
};

const getAmount = async (signerAddress, token) => {
  const amountWei = await token.balanceOf(signerAddress);
  const amount = web3.utils.fromWei(web3.utils.toBN(amountWei)).toString();
  console.log(`amount: ${amount}`);
  return amount;
};

const nftpage = ({ signerAddress, nfttoken, token }) => {
  return (
    <div>
      <h1>NFT</h1>
      <br />

      <div className="card col-sm-6 bg-secondary">
        Token address: 0x5FbDB2315678afecb367f032d93F642f64180aa3 install
        Metamask, and create an account. send some tokens to your account. (this
        is all free on the test network!)
      </div>
      <div>{`Balance: ${getAmount(signerAddress, token)} `}</div>
      <div className="card col-sm-6 bg-secondary">
        <div className="card-body">
          <div className="card-header">
            Faucet NFT Token: (send some tokens to your address)
          </div>
          <form>
            Address:
            <input type="text" className="form-control" placeholder="" />
            <br />
            <button onClick={onClickFunction} className="btn btn-primary">
              submit
            </button>
          </form>
        </div>
      </div>

      <div className="card col-sm-6 bg-secondary">
        <div className="card-body">
          <div className="card-header">Add artist to NFT</div>
          <form>
            Address:
            <input type="text" className="form-control" placeholder="" />
            <br />
            <button type="submit" className="btn btn-primary">
              submit
            </button>
          </form>
        </div>
      </div>

      <div className="card col-sm-6 bg-secondary">
        <div className="card-body">
          <div className="card-header">Send the artist NFT</div>
          <form>
            Address:
            <input
              name="address"
              type="text"
              className="form-control"
              placeholder=""
            />
            Amount:
            <input
              name="amount"
              type="text"
              className="form-control"
              placeholder=""
            />
            <br />
            <button type="submit" className="btn btn-primary">
              submit
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default nftpage;
