import { ethers, Contract } from "ethers";
//import Web3 from "web3";
import MyToken from "../contracts/MyToken.json";

const [currentchainid, setCurrentChainID] = useState(0);
const [currentaccount, setCurrentAccount] = useState(0);

const getBalance = async (token, account) => {
  const balance = await token.balanceOf(account);
  return ethers.utils.formatEther(balance);
};

const ConnectWallet = async () => {
  console.log("Try Connect");

  try {
    await window.ethereum.enable();

    const id = await window.ethereum.request({ method: "eth_chainId" });
    setCurrentChainID(() => parseInt(id, 16));

    const accounts = await window.ethereum.request({
      method: "eth_requestAccounts",
    });
    setIsLogged(true);
    setCurrentAccount(accounts[0]);

    console.log(accounts);
    return { currentaccount, currentchainid };
  } catch (err) {
    if (err.code === 4001) {
      // EIP-1193 userRejectedRequest error
      // If this happens, the user rejected the connection request.
      console.log("Please connect to MetaMask.");
      setMessage((messages) => [
        ...messages,
        {
          head: "User Rejected Request",
          body: "Please connect to MetaMask.",
          variant: "info",
        },
      ]);
    } else if (err.code === -32002) {
      console.log("Please unlock MetaMask.");
      setMessage((messages) => [
        ...messages,
        {
          head: "User Request Pending",
          body: "Please unlock MetaMask and try agin.",
          variant: "info",
        },
      ]);
    } else {
      console.error(err);
      setMessage((messages) => [
        ...messages,
        { head: "Error", body: err.message, variant: "info" },
      ]);
    }
  }
};

const getTokenBlockchain = () =>
  new Promise((resolve, reject) => {
    window.addEventListener("load", async () => {
      console.log(`window.ethereum: ${window.ethereum}`);
      if (window.ethereum) {
        await window.ethereum.enable();
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        // console.log(`provider: ${provider}`);
        const signer = provider.getSigner();
        // console.log(`signer:${signer}`);
        const signerAddress = await signer.getAddress();
        // console.log(`signerAddress:${signerAddress}`);
        const mytoken = new Contract(MyToken.address, MyToken.abi, signer);
        //const balance = getBalance(mytoken, signerAddress);
        const balance = await mytoken.balanceOf(signerAddress);
        //console.log(balance);
        console.log(ethers.utils.formatEther(balance));

        //const contract = instance();

        let artist = await mytoken._artist();

        const percentage = ethers.utils.formatEther(
          await mytoken._percentage()
        );

        const admin = await mytoken.admin();

        if (artist === "0x0000000000000000000000000000000000000000") {
          artist = null;
        }

        console.log(artist);
        console.log(percentage);
        console.log(admin);

        resolve({
          signerAddress,
          mytoken,
          addresses: [artist, percentage, admin],
        });
      }
      resolve({
        signerAddress: undefined,
        mytoken: undefined,
        addresses: [],
      });
    });
  });

export { getTokenBlockchain, getBalance, ConnectWallet };
