import { ethers, Contract } from 'ethers';
import Token from '../contracts/MockToken.json';
import nftToken from '../contracts/NFT.json';

const getTokenBlockchain = () =>
  new Promise((resolve, reject) => {
    window.addEventListener('load', async () => {
      console.log(`window.ethereum: ${window.ethereum}`);
      if(window.ethereum) {
        await window.ethereum.enable();
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        console.log(`provider: ${provider}`);
        const signer = provider.getSigner();
        console.log(`signer:${signer}`);
        const signerAddress = await signer.getAddress();
        console.log(`signerAddress:${signerAddress}`);


        const token = new Contract(
          Token.address,
          Token.abi,
          signer
        );

        const nfttoken = new Contract(
          nftToken.address,
          nftToken.abi,
          signer
        );

        console.log(`token:${token}`);
        console.log(`nfttoken:${nfttoken}`);
        resolve({signerAddress, token, nfttoken});
      }
      resolve({signerAddress: undefined, token: undefined});
    });
  });

export default getTokenBlockchain;
