const nftpage = ({ signerAddress, nfttoken, token, amount }) => {
  return (
    <div>
      <h1>NFT</h1>
      <br />

      <div className="card col-sm-6 bg-secondary">
        Token address: 0x5FbDB2315678afecb367f032d93F642f64180aa3 install
        Metamask, and create an account. send some tokens to your account. (this
        is all free on the test network!)
      </div>
      <div>{`Balance: ${amount} `}</div>
      <div className="card col-sm-6 bg-secondary">
        <div className="card-body">
          <div className="card-header">
            Faucet NFT Token: (send some tokens to your address)
          </div>
          <form>
            Address:
            <input type="text" className="form-control" placeholder="" />
            <br />
            <button onClick={onClickFunction} className="btn btn-primary">
              submit
            </button>
          </form>
        </div>
