      <div className="card col-sm-6 bg-secondary">
        <div className="card-body">
          <div className="card-header">Add artist to NFT</div>
          <form>
            Address:
            <input type="text" className="form-control" placeholder="" />
            <br />
            <button type="submit" className="btn btn-primary">
              submit
            </button>
          </form>
        </div>
      </div>

      <div className="card col-sm-6 bg-secondary">
        <div className="card-body">
          <div className="card-header">Send the artist NFT</div>
          <form>
            Address:
            <input
              name="address"
              type="text"
              className="form-control"
              placeholder=""
            />
            Amount:
            <input
              name="amount"
              type="text"
              className="form-control"
              placeholder=""
            />
            <br />
            <button type="submit" className="btn btn-primary">
              submit
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default nftpage;
