import { ethers, Contract } from "ethers";
import Web3 from "web3";
import MyToken from "../contracts/MyToken.json";

// const getArtist = async (mytoken) => {
//   return await mytoken._artist;
// };

const getContract = async () => {
  const data = await MyToken;
  const web3 = new Web3(
    new Web3.providers.HttpProvider("http://localhost:8545")
  );
  const netId = await web3.eth.net.getId();
  //const deployedNetwork = data.networks[netId];
  const greeting = new web3.eth.Contract(
    data.abi,
    deployedNetwork && deployedNetwork.address
  );
  return greeting;
};

const getTokenBlockchain = () =>
  new Promise((resolve, reject) => {
    window.addEventListener("load", async () => {
      console.log(`window.ethereum: ${window.ethereum}`);
      if (window.ethereum) {
        await window.ethereum.enable();
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        // console.log(`provider: ${provider}`);
        const signer = provider.getSigner();
        // console.log(`signer:${signer}`);
        const signerAddress = await signer.getAddress();
        // console.log(`signerAddress:${signerAddress}`);
        const mytoken = new Contract(MyToken.address, MyToken.abi, signer);
        //const balance = getBalance(mytoken, signerAddress);
        const balance = await mytoken.balanceOf(signerAddress);
        //console.log(balance);
        console.log(ethers.utils.formatEther(balance));

        const contract = getContract();

        //const artist = await mytoken.getArtist();
        console.log(contract);
        // const contract_fac = await mytoken._artist();
        // console.log(contract_fac);
        // const artist = getArtist(mytoken);
        // console.log(artist);
        // console.log(`token:${token.balanceOf(signerAddress)}`);

        resolve({ signerAddress, mytoken });
      }
      resolve({
        signerAddress: undefined,
        mytoken: undefined,
      });
    });
  });

export { getTokenBlockchain };
