// Import the ethers library
import React from "react";
import { ethers } from "ethers";
import "bootstrap/dist/css/bootstrap.css";
import "bootstrap/dist/css/bootstrap-grid.css";

const transfertokens = ({ signerAddress, mytoken, currentAccount }) => {
  let balfrom, balto;

  const getBalance = async (e) => {
    e.preventDefault();
    const from = e.target.elements.from.value;
    const to = e.target.elements.to.value;
    //const amount = e.target.elements.amount.value;
    // console.log(mytoken);
    // console.log(from);
    // console.log(to);
    // console.log(amount);
    balto = await mytoken.balanceOf(to);
    balfrom = await mytoken.balanceOf(from);

    console.log(ethers.utils.formatEther(balto));
    console.log(ethers.utils.formatEther(balfrom));
  };

  const onClickFunction = async (e) => {
    e.preventDefault();
    getBalance(e);
    //const from = e.target.elements.from.value;
    const to = e.target.elements.to.value;
    const amount = e.target.elements.amount.value;
    // console.log(mytoken);
    // console.log(from);
    // console.log(to);
    // console.log(amount);
    await mytoken.transfer(to, ethers.utils.parseUnits(amount));
  };

  const MintTokens = async (e) => {
    e.preventDefault();
    //const to = e.target.elements.to.value;
    const amount = e.target.elements.amount.value;
    // console.log(mytoken);
    // console.log(from);
    // console.log(to);
    // console.log(amount);
    await mytoken.mint(ethers.utils.parseUnits(amount));
  };

  const onTransferTokensWithoutRoyalties = async (e) => {
    e.preventDefault();
    const from = e.target.elements.from.value;
    const to = e.target.elements.to.value;
    const amount = e.target.elements.amount.value;
    console.log(from);
    console.log(to);

    await mytoken.transfer(to, ethers.units.parseEther(amount));
  };

  return (
    <div>
      <h1>Transfer Tokens Without Royalties</h1>
      <br />

      <div className="card col-sm-8 bg-secondary">
        Token address: 0x5FbDB2315678afecb367f032d93F642f64180aa3 install
        Metamask, and create an account. send some tokens to your account. (this
        is all free on the test network!)
      </div>
      <br />
      <div className="col-4  card col-sm-8 bg-secondary">
        <div className="card-body">
          <div className="card-header">Transfer Tokens Without Royalties.</div>
          <form onSubmit={(e) => onTransferTokensWithoutRoyalties(e)}>
            From address:
            <input
              type="readonly"
              name="from"
              className="form-control"
              placeholder=""
              value={currentAccount}
            />
            To address:
            <input
              type="text"
              name="to"
              className="form-control"
              placeholder=""
            />
            Amount:
            <input
              type="text"
              name="amount"
              className="form-control"
              placeholder=""
            />
            <br />
            <button className="btn btn-primary">submit</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default transfertokens;
